    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background-color: var(--secondary-color);
    border: none;
    font-size: 2rem; 
    color: var(--bg-color);
    padding: 0; /* Remove padding */
    width: 50px; /* Set explicit width */
    height: 50px; /* Set explicit height */
    border-radius: 50%; /* Ensure buttons are circular */
    display: flex; /* Use flexbox for alignment */
    align-items: center; /* Center the content vertically */
    justify-content: center; /* Center the content horizontally */
    cursor: pointer;
    z-index: 1;
    transition: background-color 0.3s;
}

.carousel-button:hover {
    background-color: var(--accent-color);
}

@media (max-width: 768px) {
    .carousel-button {
        width: 40px; /* Smaller button size on mobile */
        height: 40px; /* Smaller button size on mobile */
        font-size: 1.5rem; /* Smaller font size on mobile */
    }
}

/* Adjust carousel image and text for better mobile experience */
.carousel-item img {
    max-height: 300px; /* Limit max height on mobile */
}

.carousel-item h3, .carousel-item p {
    font-size: 1rem; /* Adjust font size on mobile */
}

